import React from 'react';
import { useState } from 'react';
import './gamePage.scss';

function GamePage() {
    const [guess, setGuess] = useState('');//we make a variable for the users guess
    const [riddle, setRiddle] = useState('');//we make a variable to store the riddle
    const [city, setCity] = useState('');//can change this dynamically 
    const [status, setStatus] = useState('');
    const [language, setLanguage] = useState('');//set the language
    const [cityresponse, originalData] = useState('');

    //put this in a different file
    function FallAwayButton() {
      const [clicked, setClicked] = useState(false);
    
      const handleClick = () => {
        setClicked(true);
      };
    
      return (
        <div className="fall-container">
          <button
            className={`fall-button ${clicked ? 'fall' : ''}`}
            onClick={handleClick}
            disabled={clicked}
          >
            Click Me!
          </button>
    
          <p className={`hidden-text ${clicked ? 'show' : ''}`}>
            
          </p>
        </div>
      );
    }
    

    //
    const getRiddle = async () => {
        try {
            const cityresponse = await fetch('/api/getRandomCity');
            const citydata = await cityresponse.json();
            originalData(citydata);
            console.log(citydata.city_name)
            setCity(citydata.city_name);
            setLanguage(citydata.language);
            
            const riddleResponse = await fetch('/api/generateRiddle', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ cityName: citydata.city_name })
            });
            const riddledata = await riddleResponse.json();
            setRiddle(riddledata.riddle);
            setStatus('');
            setGuess('');
        } catch (err) {
            console.error('Error fetching riddle', err);
            setRiddle('Error loading riddle.');
        }
    };

    const handleSubmit = () => {
        if (guess.toLowerCase() === city.toLowerCase()) {
            setStatus('✅ Correct!');
        } else {
            getDistance(); // Call the new function
        }
    };
    

    const getDistance = async () => {
        try {
            const response = await fetch(`/api/${guess}`);
            if (!response.ok) throw new Error('City not found.');
    
            const guessedCity = await response.json();
    
            const distanceResponse = await fetch('/api/distance', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    city1: guessedCity,
                    city2: cityresponse
                })
            });
    
            const distanceData = await distanceResponse.json();
            setStatus(`❌ Try again! You are ${distanceData.distance.toFixed(2)} km away.`);
        } catch (err) {
            console.error('Error calculating distance:', err);
            setStatus('❌ Error getting distance. Try a valid city name.');
        }
    };



    

    return (
        <div className = "game">
            <h2> Welcome to City Riddler</h2>
            <button onClick={getRiddle}>Generate Riddle</button>
            {riddle && (
                <div className="riddle-section">
                <p><strong>Riddle:</strong> {riddle}</p>
                <input
                    type="text"
                    value={guess}
                    onChange={(e) => setGuess(e.target.value)}
                    placeholder="Your guess here..."
                />
                
                <button onClick={handleSubmit}>Submit Guess</button>
                <p className="status">{status}</p>
                </div>
            )}
            <div>

            </div>
        </div>
    );
}

export default GamePage;
