import React from 'react';
import { useState } from 'react';
import './gamePage.scss';

function GamePage() {
    const [guess, setGuess] = useState('');//we make a variable for the users guess
    const [riddle, setRiddle] = useState('');//we make a variable to store the riddle
    const [city, setCity] = useState('');//can change this dynamically 
    const [status, setStatus] = useState('');
    const [language, setLanguage] = useState('');//set the language

    const getRiddle = async () => {
        try {
            const cityresponse = await fetch('/api/getRandomCity');
            const citydata = await cityresponse.json();
            console.log(citydata.city_name)
            setCity(citydata.city_name);
            setLanguage(citydata.language);
            
            const riddleResponse = await fetch('/api/generateRiddle', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ cityName: citydata.city_name })
            });
            const riddledata = await riddleResponse.json();
            setRiddle(riddledata.riddle);
            setStatus('');
            setGuess('');
        } catch (err) {
            console.error('Error fetching riddle', err);
            setRiddle('Error loading riddle.');
        }
    };

    const handleSubmit = () => {
        if(guess.toLowerCase() === city.toLowerCase()) {
            setStatus('✅ Correct!');
        } else {
            setStatus('❌ Try again!');
        }
    };

    const checker = () => {
        
    };

    return (
        <div className = "game">
            <h2> Welcome to City Riddler</h2>
            <button onClick={getRiddle}>Generate Riddle</button>
            {riddle && (
                <div className="riddle-section">
                <p><strong>Riddle:</strong> {riddle}</p>
                <input
                    type="text"
                    value={guess}
                    onChange={(e) => setGuess(e.target.value)}
                    placeholder="Your guess here..."
                />
                <button onClick={handleSubmit}>Submit Guess</button>
                <p className="status">{status}</p>
                </div>
            )}
            <div>

            </div>
        </div>
    );
}

export default GamePage;
